<?php
require "config/db.php";

$sql = "SELECT * FROM products WHERE 1=1";

if (!empty($_POST['categories'])) {
    $cats = $_POST['categories'];
    $cats_list = "'" . implode("','", $cats) . "'";
    $sql .= " AND category IN ($cats_list)";
}

if (!empty($_POST['brand'])) {
    $sql .= " AND brand = '" . $_POST['brand'] . "'";
}

if (!empty($_POST['min_price']) && is_numeric($_POST['min_price'])) {
    $sql .= " AND price >= " . $_POST['min_price'];
}

if (!empty($_POST['max_price']) && is_numeric($_POST['max_price'])) {
    $sql .= " AND price <= " . $_POST['max_price'];
}

$result = mysqli_query($conn, $sql);

if (mysqli_num_rows($result) > 0) {
    while ($row = mysqli_fetch_assoc($result)) {
        // Use manual image URLs from web instead of local images
        // Map product IDs or categories to external image URLs (example)
        $images = [
            1 => "https://encrypted-tbn2.gstatic.com/shopping?q=tbn:ANd9GcTkOIwiiRZ0vYBB-oSKkR9UGv7f6Bp-Z_OjBvwJVxPfYu_QVIfvilixxpm-1W-l92Rfhfpf0hsTGvo8H_5BkRdAGbtCboiMlZlMqkYc2D0h",
            2 => "data:image/webp;base64,",
            3 => "data:image/jpeg;base64,",
            4 => "data:image/webp;base64,"
          //  5 => "data:image/jpeg;base64,",
            //6 => "data:image/jpeg;base64,",
        ];

        $img_url = isset($images[$row['id']]) ? $images[$row['id']] : "https://via.placeholder.com/300x200?text=No+Image";

        echo "
        <div class='col-md-4 mb-4'>
            <div class='card shadow-sm h-100'>
                <img src='{$img_url}' class='card-img-top' alt='{$row['name']}'>
                <div class='card-body d-flex flex-column'>
                    <h5 class='card-title'>{$row['name']}</h5>
                    <p class='card-text text-success fw-bold'>₹{$row['price']}</p>
                    <a href='product.php?id={$row['id']}' class='btn btn-primary mt-auto'>View & Buy</a>
                </div>
            </div>
        </div>
        ";
    }
} else {
    echo "<div class='col-12 text-center py-5'><h4>No products found</h4></div>";
}
